	if (sockFd < 0)
	{
		printf("connect error\n");
		return -1;
	}
	else 
	    printf("connect success\n");
	//创建线程
	HANDLE hand1 = CreateThread(NULL, 0, Fun2, &sockFd , 0, NULL);
	HANDLE hand2 = CreateThread(NULL, 0, Fun1, &sockFd , 0, NULL);

	WaitForSingleObject(hand1, INFINITE);

        closesocket(sockFd);
	return 0;
}
