        int len = nums.size();
        int x = 0, y = 0;
        for(int i = 0; i < len; i++){
            if(nums[i] == 0) x++;
            else if(nums[i] == 1) y++;
        }
        int i = 0;
        len = len - x - y;
        while(x){
            nums[i++] = 0;
            x--;
        }
        while(y){
            nums[i++] = 1;
            y--;
        }
        while(len){
            nums[i++] = 2;
            len--;
        }
    }
};
